mapper.insert:
INSERT	INTO COMMUNITY (
	ID
	, USER_ID
	, TITLE
	, LYRICS
	, RELEASE_DATE
	, REQUEST_IP
	, SINGER
	, ALBUM
	, GENRE
	, DISPLAY_FILE_NAME
	, SING_IMG
)
VALUES	(
	COMMUNITY_ID_SEQ.NEXTVAL
	, #{userId}
	, #{title}
	, #{lyrics}
	, SYSDATE
	, #{requestIp}
	, #{singer}
	, #{album}
	, #{genre}
	, #{displayFilename}
	, #{singImg}
)

mapper.update:
UPDATE	COMMUNITY
SET		VIEW_COUNT = VIEW_COUNT + 1
WHERE	ID = #{id}

UPDATE	COMMUNITY
SET		lIKE_IT = lIKE_IT + 1
WHERE	ID = #{id}

mapper.select:
SELECT	COUNT(ID)
FROM	COMMUNITY
WHERE	USER_ID=#{userId}
